/// <summary>
/// Create a Bitmap.
/// </summary>
public void CreateBitmap()
{
    IMvxBitmap bitmap = this.bitmapFactory.Create();

    MvxResourcePath resourcePath = new MvxResourcePath("BitmapTest.Core.Assets.droidking.jpeg");
    bitmap.LoadFromResource("BitmapTest.Core", resourcePath);
}
